;; **
;;; ## Scaling the Size and Text of Bubble Charts
;; **

;; @@
(-> (plotly {:x (vec (range 4)) 
             :y [10 11 12 13]
             :s [400 600 800 1000]})
    (add-scatter :mode "markers"
                 :marker {:size :s
                          :sizemode "area"})
    (add-scatter :mode "markers"
                 :y #(m/add (d/column % :y) 4)
                 :marker {:size #(m/mul (d/column % :s) 0.2)
                          :sizemode "area"})
    (add-scatter :mode "markers"
                 :y #(m/add (d/column % :y) 8)
                 :marker {:size #(m/mul (d/column % :s) 2)
                          :sizemode "area"})
    iplot)
