(def data 
  (let [in-file (slurp "https://raw.githubusercontent.com/plotly/datasets/master/gapminderDataFiveYear.csv")
        data (doall (csv/read-csv in-file))]
    (d/dataset (map keyword (first data)) (rest data))))
