;; @@
(let [n 200]
  (-> (plotly)
      (add-scatter :x (rnd/sample-normal n) 
                   :y (m/add 2 (rnd/sample-normal n))
                   :mode "markers"
                   :name "above"
                   :marker {:size 10
                            :color "rgba(152, 0, 0, .8)"
                            :line {:width 2
                                   :color "rgb(0, 0, 0)"}})
      (add-scatter :x (rnd/sample-normal n)
                   :y (m/add -2 (rnd/sample-normal n))
                   :mode "markers"
                   :name "below"
                   :marker {:color "rgba(255, 182, 193, .9)"
                            :size 10
                            :line {:width 2}})
      (set-layout :xaxis {:zeroline false}
                  :yaxis {:zeroline false}
                  :title "Styled Scatter")
      iplot))
