(defn parse-float 
  [x]
  (Float/parseFloat x))

(defn mark-usa
  [p i]
  (-> p
    (add-scatter :x :rank 
                 :y #(m/add (* i 1000000) (map parse-float (d/column % :pop)))
                 :mode "markers"
                 :marker {:size 14
                          :color (str "hsl(" (float (* i (/ 360 (count usa-states)))) ",50%,50%)")
                          :line {:width 1}
                          :opacity 0.3}
                 :name (+ 2000 i)
                 :text (nth (d/column usa-states :state) i))))

(-> (reduce mark-usa
            (plotly usa-states) 
            (range (count usa-states)))
    (set-layout :showlegend false
                :hovermode "closest")
    iplot)
